package lesson1;

public class WhileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* 
		  a piece of code or statement which gets executed again ang again
		  without writing it 
		
		 */
		int a =0;
		while(a<= -1)
		{
			
			System.out.println(a);
			a++;
			
		}
		System.out.println("----------------");
		int b=0;
		do{
			System.out.println(b );
			b++;
			
		}while(b<= -1);
		
		
	}

}
